var htmlContent = "<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\" >\r\n<head>\r\n\r\n\t<meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\" />\r\n\t<!--[if !mso]><!-->\r\n\t<meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge\" />\r\n\t<!--<![endif]-->\r\n\t<meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\" />\r\n\t<title>Teatro Colegios</title>\r\n\t<style type=\"text/css\">\r\n\t\r\n\t\t/* ===> Importing Fonts <=== */\r\n\t\t@import url(https://fonts.googleapis.com/css?family=Open+Sans:300,400,600,700,800);\r\n\t\t@import url(https://fonts.googleapis.com/css?family=Quicksand:300,400,500,700);\r\n\t\r\n\t\t/* ===> Global CSS <=== */\r\n\t\t.ReadMsgBody { width: 100%; background-color: #FFFFFF; }\r\n\t\t.ExternalClass { width: 100%; background-color: #FFFFFF; }\r\n\t\t.ExternalClass, .ExternalClass p, .ExternalClass span, .ExternalClass font, .ExternalClass td, .ExternalClass div { line-height: 100%; }\r\n\t\thtml { width: 100%; }\r\n\t\tbody { -webkit-text-size-adjust: none; -ms-text-size-adjust: none; margin: 0; padding: 0; font-family: 'Open Sans', Arial, Sans-serif !important; }\r\n\t\ttable { border-spacing: 0; table-layout: auto; margin: 0 auto; }\r\n\t\timg { display: block !important; overflow: hidden !important; }\r\n\t\t.yshortcuts a { border-bottom: none !important; }\r\n\t\ta { color: #aaaaaa; text-decoration: none; }\r\n\t\t.ava_text_button a { font-family: 'open sans', arial, sans-serif !important; }\r\n\t\t.ava_button_link a { color:#FFFFFF !important; }\r\n\t\r\n\t\t/* ===> Responsive CSS For Tablets <=== */\r\n\t\t@media only screen and (max-width: 640px) {\r\n\t\t\tbody { margin: 0px; width: auto !important; font-family: 'Open Sans', Arial, Sans-serif !important;}\r\n\t\t\t.inner_tab { width: 90% !important;  max-width: 90%!important;}\r\n\t\t\t.outer_tab { width: 100%!important; max-width: 100%!important; text-align: center !important;}\r\n\t\t}\r\n\t\r\n\t\t/* ===> Responsive CSS For Phones <=== */\r\n\t\t@media only screen and (max-width: 479px) {\r\n\t\t\tbody { width: auto !important; font-family: 'Open Sans', Arial, Sans-serif !important;}\r\n\t\t\t.inner_tab { width: 90% !important; text-align: center !important;}\r\n\t\t\t.outer_tab { width: 100%!important; max-width: 100%!important; text-align: center !important;}\r\n\t\t\t.auto_line { width: 100%!important; max-width: 100%!important; text-align: center !important;}\r\n\t\t}\r\n\t\r\n\t</style>\r\n\r\n</head>\r\n<body>\r\n<table width=\"100%\" align=\"center\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\r\n\r\n\r\n        <table class=\"full\" bgcolor=\"#FFFFFF\" align=\"center\" width=\"100%\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\r\n                <tr>\r\n                    <td align=\"center\" bgcolor=\"#ffffff\">\r\n                        <table align=\"center\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\r\n                            <tr>\r\n                                <td height=\"60\"></td>\r\n                            </tr>\r\n                            <tr>\r\n                                <td align=\"center\" width=\"600\">\r\n                                    <table class=\"inner_tab\" width=\"100%\" border=\"0\" align=\"center\" cellpadding=\"0\" cellspacing=\"0\">\r\n                                        \r\n                                        <!--   Title   -- START -->\r\n                                        <tr>\r\n                                            <td  align=\"center\" style=\"font-family: 'Quicksand', Arial, sans-serif; font-size: 22px; font-weight: 500; color: #888888; letter-spacing: 0.5px; line-height: 25px;\">\r\n                                                \"Teatro de títeres, Me Quitaron el Dulce: explorando la Magia de la Convivencia en el Jardín. Juntos por una Inclusión Escolar Sin Bullying\"\r\n                                            </td>\r\n                                        </tr>\r\n                                        <!--   Title   --- END --->\r\n                                        \r\n                                        <tr>\r\n                                            <td height=\"20\"></td>\r\n                                        </tr>\r\n                                        \r\n                                        <!--   Image   -- START -->\r\n                                        <tr>\r\n                                            <td align=\"center\" style=\"line-height: 0px;\">\r\n                                                <a href=\"https://youtu.be/a3MtbpbhXCI\"> <img src=\"https://teatrocolegios.cl/wp-content/uploads/post-clementina.jpg\"  alt=\"show navideño\" width=\"100%\" height=\"auto\" style=\"display:block; line-height:0px; font-size:0px; border:0px;\" /></a>\r\n                                            </td>\r\n                                        </tr>\r\n                                        <!--   Image   --- END --->\r\n                                        \r\n                                        <tr>\r\n                                            <td height=\"20\"></td>\r\n                                        </tr>\r\n                                        \r\n                                        <!--   Content   -- START -->\r\n                                        <tr>\r\n                                            <td  align=\"center\" style=\"font-family: 'open sans', Arial, sans-serif; font-size: 15px; color: #888888; letter-spacing: .5px; line-height: 23px;\">\r\n                                                En la obra titulada \"Me Quitaron el Dulce\", se aborda la temática de la inclusión educativa y social, la prevención del bullying y la promoción de la convivencia escolar. Mediante el uso de la magia de los títeres, se presenta una situación que ejemplifica una conducta inapropiada, la cual logra ser modificada gracias a la colaboración de todos los niños participantes.\r\n\r\n                                                                                        El objetivo de esta obra es sensibilizar a los niños y niñas sobre la importancia de respetar las diferencias individuales, fomentar la empatía y promover un ambiente de convivencia saludable en el entorno escolar. A través del uso de títeres, se recrea una situación conflictiva en la que se evidencia un comportamiento inadecuado por parte de uno de los personajes.\r\n                                                                                        \r\n                                                                                        Durante el desarrollo de la obra, se invita a los niños a reflexionar sobre las consecuencias negativas que puede acarrear el bullying y se les enseña estrategias de resolución pacífica de conflictos. A través de la participación activa de todos los niños, se busca generar un espacio de diálogo y colaboración para lograr un cambio positivo en la conducta del personaje involucrado.\r\n                                                                                        \r\n                                                                                        Es importante resaltar que la obra \"Me Quitaron el Dulce\" se presenta como una herramienta pedagógica efectiva para educadoras de jardines infantiles. Su enfoque lúdico y participativo permite captar la atención de los niños y promover la reflexión y el aprendizaje significativo en torno a temas relevantes como la inclusión, el respeto y la convivencia escolar.\r\n                                                                                        \r\n                                                                                        Mediante esta propuesta teatral, se busca no solo entretener, sino también educar y formar a los niños desde temprana edad, cultivando valores fundamentales para su desarrollo integral. La magia de los títeres se convierte en una poderosa herramienta para transmitir mensajes positivos y promover una sociedad más inclusiva y respetuosa.\r\n                                                                                        \r\n                                                                                        En conclusión, \"Me Quitaron el Dulce\" se presenta como una obra que aborda de manera efectiva la inclusión educativa y social, la prevención del bullying y la promoción de la convivencia escolar. A través de la magia de los títeres, se logra generar conciencia en los niños sobre la importancia de respetar las diferencias, fomentar la empatía y construir un entorno escolar en el que prime el respeto mutuo y la convivencia pacífica.\r\n\r\n\r\n                                            </td>\r\n                                        </tr>\r\n                                        <!--   Content   --- END --->\r\n                                        \r\n                                        <tr>\r\n                                            <td height=\"20\"></td>\r\n                                        </tr>\r\n                                        \r\n                                        <!--   Button   -- START -->\r\n                                        <tr>\r\n                                            <td align=\"center\">\r\n                                                <table border=\"0\" align=\"center\" cellpadding=\"0\" cellspacing=\"0\" class=\"textbutton\">\r\n                                                    <tr>\r\n                                                        <td  class=\"ava_button_link\" bgcolor=\"#793268\" height=\"44\" align=\"center\" style=\"font-family: 'Open Sans', Arial, sans-serif; font-size: 15px; font-weight: 600; color: #FFFFFF; letter-spacing: .7px; border: 0px solid #ffffff; padding-left: 25px; padding-right: 25px; border-radius: 3px;\">\r\n                                                            <a href=\"https://teatrocolegios.cl/contacto-2/\">Cotizar</a>\r\n                                                        </td>\r\n                                                    </tr>\r\n                                                </table>\r\n                                            </td>\r\n                                        </tr>\r\n                                        <!--   Button   --- END --->\r\n                                        \r\n                                    </table>\r\n                                </td>\r\n                            </tr>\r\n                            <tr>\r\n                                <td height=\"60\"></td>\r\n                            </tr>\r\n                        </table>\r\n\r\n\r\n\r\n                        \r\n\t\t\t</td>\r\n\t\t</tr>\r\n\t</table>\r\n\t<!-- ========================================= -->\r\n\t<!--                Wide Image                 -->\r\n\t<!-- ===========================> ENDING <==== -->\r\n\r\n\r\n\r\n\t<!-- ====> STARTING <========================= -->\r\n\t<!--              Contact Section              -->\r\n\t<!-- ========================================= -->\r\n\t<table class=\"full\" bgcolor=\"#FFFFFF\" align=\"center\" width=\"100%\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\r\n\t\t<tr>\r\n\t\t\t<td align=\"center\" bgcolor=\"#282828\">\r\n\t\t\t\t<table class=\"inner_tab\" width=\"600\" style=\"max-width: 600px;\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\r\n\t\t\t\t\t<tr>\r\n\t\t\t\t\t\t<td height=\"60\"></td>\r\n\t\t\t\t\t</tr>\r\n\t\t\t\t\t<tr>\r\n\t\t\t\t\t\t<td>\r\n\t\t\t\t\t\t\t\r\n\t\t\t\t\t\t\t<!-- ======= SECTION : LEFT =======> START -->\r\n\t\t\t\t\t\t\t<table width=\"290\" align=\"left\" class=\"outer_tab\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\r\n\t\t\t\t\t\t\t\t\r\n\t\t\t\t\t\t\t\t<!--   Title   -- START -->\r\n\t\t\t\t