    // Add more recipients here
};

// Ruta del archivo Excel
string filePath = "jardines.xlsx";
ExcelPackage.LicenseContext = LicenseContext.NonCommercial;

// Cargar el archivo Excel
FileInfo fileInfo = new FileInfo(filePath);
using (ExcelPackage package = new ExcelPackage(fileInfo))
{
ExcelWorksheet worksheet = package.Workbook.Worksheets[0];

int rowCount = worksheet.Dimension.Rows;
int columnCount = worksheet.Dimension.Columns;

// Recorrer el archivo Excel y agregar los correos a la lista
for (int row = 1; row <= rowCount; row++)
{
string email = worksheet.Cells[row, 1].Value?.ToString(); // Suponiendo que los correos están en la columna A
if (!string.IsNullOrEmpty(email))
{
recipientList.Add(email);
}
}
}


try
{
    Console.WriteLine("Sending emails...");

    foreach (var recipient in recipientList)
    {
        try
        {
            Console.WriteLine($"Sending email to {recipient}...");

            EmailSendOperation emailSendOperation = await emailClient.SendAsync(
                Azure.WaitUntil.Completed,
                sender,
                recipient,
                subject,
                htmlContent);

            EmailSendResult emailSendResult = emailSendOperation.Value;

            Console.WriteLine($"Email sent to {recipient}. Status = {emailSendResult.Status}");
            Console.WriteLine($"Email operation id = {emailSendOperation.Id}");
        }
        catch (RequestFailedException ex)
        {
            Console.WriteLine($"Error sending email to {recipient}: {ex.Message}");

            // Handle the error as desired (e.g., logging, updating Excel, etc.)
        }
    }

    Console.WriteLine("All emails sent successfully.");
}
catch (Exception ex)
{
    Console.WriteLine($"Email send operation failed: {ex.Message}");
}
